mapper.select:
SELECT goods_id,goods_name,goods_category_id,goods_cover_img,selling_price,create_time FROM tb_newbee_mall_goods_info WHERE goods_category_id=${goodsCategoryId}
ORDER BY ${orderBy} DESC
ORDER BY ${orderBy} ASC
LIMIT 10 OFFSET ${offsetId};

SELECT
	goods_id,goods_name,goods_category_id,goods_cover_img,selling_price,create_time
FROM
	tb_newbee_mall_goods_info
WHERE
	goods_category_id=${goodsCategoryId}

ORDER BY ${orderBy} ${ascOrDesc}

LIMIT ${limit} OFFSET ${offSet};

SELECT goods_id,goods_name,goods_category_id,goods_cover_img,selling_price,create_time,col1,col1_name,col2,col2_name,col3,col3_name FROM tb_newbee_mall_goods_info WHERE goods_category_id IN (SELECT category_id FROM newbee_mall_db.tb_newbee_mall_goods_category WHERE parent_id = ${goodsCategoryId})
UNION
SELECT goods_id,goods_name,goods_category_id,goods_cover_img,selling_price,create_time,col1,col1_name,col2,col2_name,col3,col3_name FROM tb_newbee_mall_goods_info WHERE goods_category_id=${goodsCategoryId}
ORDER BY ${orderBy} ${ascOrDesc} LIMIT ${limit} OFFSET ${offSet};
